SELECTA.*FROM(select C.CategoryName as CATEGORIA , P.ProductName AS PRODUCTO,    count(OD.ProductID) as Cantidad       from [Northwind].[dbo].[Categories] as Cinner join [Northwind].[dbo].[Products] as P on C.CategoryID=P.CategoryID inner join [Northwind].[dbo].[Order Details] as OD onOD.ProductID=P.ProductID group by C.CategoryName, P.ProductName ) as A JOIN(SELECT CATEGORIA ,MAX(CANTIDAD) AS MAX_CANTIDADFROM (   select C.CategoryName as CATEGORIA , P.ProductName AS PRODUCTO,    count(OD.ProductID) as Cantidad       from [Northwind].[dbo].[Categories] as Cinner join [Northwind].[dbo].[Products] as P on C.CategoryID=P.CategoryID inner join [Northwind].[dbo].[Order Details] as OD onOD.ProductID=P.ProductID group by C.CategoryName, P.ProductName   ) as Z  GROUP BY CATEGORIA) as XON (A.CATEGORIA=X.CATEGORIA)WHERE MAX_CANTIDAD=CANTIDADorder by CANTIDAD desc;